#select g.align_id,g.raw_start,d.name,g.raw_start+s.align_start-1,g.raw_start+s.align_end-1,ds.name,s.raw_start,s.raw_end,s.raw_strand,s.dnafrag_id from genomic_align_block g,dnafrag d,genomic_align_block s, dnafrag ds where ds.dnafrag_id = s.dnafrag_id and g.dnafrag_id = d.dnafrag_id and d.genome_db_id = 1 and s.align_id = g.align_id and s.dnafrag_id != g.dnafrag_id and g.align_id=?
select a.align_name,g.align_start,g.align_end,d.name,g.raw_start,g.raw_end,g.raw_strand,g.dnafrag_id,g.score,g.perc_id,g.cigar_line from genomic_align_block g,dnafrag d,align a where a.align_id=g.align_id and d.dnafrag_id=g.dnafrag_id and g.align_id=?;
